con:configuration.scriptText:
//get sku from response
import com.eviware.soapui.support.XmlHolder
def holder = new XmlHolder(messageExchange.responseContentAsXml)
def sku = holder.getNodeValue('//*:sku[contains(text(),"SKU71314")]')
context.testCase.setPropertyValue("SKU",sku)

con:config.script:
def sku = context.expand( '${TC010_GetProduct_SOAP Request#Response#//*:sku[contains(text(),"00987")]}' )
log.info "sku is $sku"
if(sku != null){
    log.info "SKU found can be deleted"
    testRunner.gotoStepByName("DeleteBySKU_SOAP Request")
}
else{
log.info "SKU not found"
}
